import React, { useEffect, useState } from 'react';
import Card from './Card';

const WebSocketComponent = ({data}) => {
  const [showRedCircle, setShowRedCircle] = useState(false);

  useEffect(() => {
    // Establish WebSocket connection
    const socket = new WebSocket('ws://localhost:8080');

    // WebSocket onmessage event
    socket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.type === 'newMessage') {
        // Set the state to show the red circle
        console.log('New message received. Setting showRedCircle to true.');
        setShowRedCircle(true);
      }
    };

    // WebSocket onclose event
    socket.onclose = () => {
      console.log('WebSocket connection closed.');
    };

    // WebSocket onerror event
    socket.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    // Clean up the WebSocket connection on component unmount
    return () => {
      socket.close();
    };
  }, []);

  const handleRedButtonClick = () => {
    setShowRedCircle(false);
  };

  console.log('showRedCircle in WebSocketComponent:', showRedCircle);

    // Function to handle data refresh
    const refreshData = () => {
      // Fetch new data from the server or any other appropriate method
      // and update the state or props of the WebSocketComponent
      // For demonstration purposes, I'll just simulate a data update with setTimeout
      setTimeout(() => {
        setShowRedCircle(false); // Set the state to hide the red circle after refresh
      }, 1000);
    };

  return (
    <div>
      {/* Pass the refreshData function to the Card component  */}
      <Card data={data}/>
      {showRedCircle && <button className="red-circle-button"> onClick={handleRedButtonClick} </button>}
    </div>
  );
};

export default WebSocketComponent;
