import React, { useState,useEffect } from 'react';
import Cards from '../components/Cards';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';
import StatBox from "../components/StatBox";
import { useTheme, Box } from "@mui/material";

const CardsScreen = () => {
  // Sample data for the cards
  const colors = tokens(theme.palette.mode);
  const [showCards, setShowCards] = useState(false);
  const [events, setEvents] = useState([]);
  const [data,setData] = useState([])
  const handleButtonClick = () => {
    setShowCards((curr)=> !curr); // This will set the showCards state to true when the button is pressed
  };
  useEffect(() => {
    // Function to fetch data from the server
    const fetchData = async () => {
      try {
        const val = 2
        const response = await fetch(`http://localhost:8000/geteventsFull?range=${val}`);

        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        const dataAns = await response.json();
        // console.log(data)
        setEvents(dataAns.events);
     


      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);
  
  
  useEffect(() => {
    // Function to fetch data from the server
    const countPerHour = async () => {     
        const hourCounts = events.reduce((acc, message) => {
          const hour = getHour(message.time);
          acc[hour] = (acc[hour] || 0) + 1;
          return acc;
        }, {})
        const datas = Object.keys(hourCounts).map((hour) => ({
          hour: `${hour}:00`,
          count: hourCounts[hour],
        }));
        setData(datas)
        // console.log(data,'her is the real data')

    };

    countPerHour();
  }, [events]);



  
  const getHour = (timestamp) => {
    const date = new Date(timestamp);

    return date.getHours();
  };
  


  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        <h2>Cards Screen</h2>
      </div>
      <Box sx={{ borderRadius: '16px', width: '98%' }}
  backgroundColor={colors.primary[400]}
  display="flex"
  alignItems="top"
  margin="20px"
  paddingTop="25px"
  paddingLeft="25px"
  height={"30vh"}
>
<div className='NeoTable' style={{ width: "100%" }}>
    <StatBox
      title="NEO Earth Close Approaches"
    />
     <div className='test' style={{ marginRight: 10 }}>
      <BarChart width={600} height={300} data={data}>
      <CartesianGrid strokeDasharray="3 3" />
      <XAxis dataKey="hour" />
      <YAxis />
      <Tooltip />
      <Legend />
      <Bar dataKey="count" fill="#8884d8" />
    </BarChart>
    </div>
    </div>
</Box>
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        <button onClick={handleButtonClick}>Show Cards</button>  
      </div>
      {showCards && <Cards data={events} />} {/* The Cards component will only render if showCards is true */}
    </div>
  );
};

export default CardsScreen;
